// Modules
/* scss-combine:../../node_modules/bc-modal/dist/scss/modal */
.modal-overlay {
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 980;
  background-color: rgba(#000, 0.3);
  display: none;
  opacity: 0;

  &.animating,
  &.visible {
    display: block;
    transition: opacity 0.3s ease;
  }

  &.visible {
    opacity: 1;
  }
}

.modal-wrapper {
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1000;
  display: none;
  overflow: hidden;
  -webkit-overflow-scrolling: touch;
  outline: 0;
  opacity: 0;

  &.animating,
  &.visible {
    display: block;
    transition: opacity 0.2s ease;
  }

  &.animating-in {
    transition-delay: 0.1s;
  }

  &.visible {
    overflow-x: hidden;
    overflow-y: auto;
    opacity: 1;
  }
}

.modal {
  position: relative;
  margin-left: auto;
  margin-right: auto;
}

/* scss-combine:../../node_modules/bc-carousel/dist/scss/carousel */
.carousel {
  position: relative;
}

.carousel-item {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  margin: 0;
  display: none;

  &.visible {
    display: block;
  }
}

.carousel-item-image {
  display: block;

  img {
    display: block;
    width: 100%;
  }
}

.carousel-item-info {
  position: absolute;
}

.carousel-navigation {
  position: absolute;
  top: 50%;
  width: 100%;
  opacity: 0;

  &.visible {
    opacity: 1;
  }
}

.carousel-navigation-item {
  position: absolute;
  cursor: pointer;

  &.carousel-previous {
    left: 0;
  }

  &.carousel-next {
    right: 0;
  }
}

.carousel-pagination {
  position: absolute;
  bottom: 2%;
  left: 2%;

  .carousel-pagination-item {
    cursor: pointer;

    &.active {
      cursor: default;
    }
  }
}

/* scss-combine:components/modals */
