// Default error classes, as dictated by validetta.js

.form-inline-message {
  display: block;

  .form-field-invalid & {
    color: $error-color;
  }
}

// ---------------------------------------------------------------- //
// Select Wrappers - Provides some additional style control for select elements

.form-select-wrapper {
  position: relative;
  display: block;
  max-width: 280px;
  min-height: 38px;
  padding: 8px 15px;
  background-color: $input-background-color;
  cursor: pointer;
  border: 1px solid $input-border-color;
  color: lighten($input-color,20%);
  font-family: $secondary-font;
  font-size: 14px;
  line-height: 1.6;

  &:after {
    position: absolute;
    top: 50%;
    right: 16px;
    display: block;
    width: 9px;
    height: 5px;
    background-image: url("data:image/svg+xml;charset=utf8,%3Csvg fill='#{escape-hex($navigation-color)}' version='1.1' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' viewBox='0 0 50 26' xml:space='preserve'%3E %3Cpath d='M47.2,0.6L24.9,22.3L3,0.6c-0.7-0.7-1.7-0.7-2.4,0s-0.7,1.7,0,2.4l22.5,22.4c0.3,0.3,0.7,0.5,1.1,0.5h1.1 c0.4,0,0.9-0.2,1.2-0.5L49.5,3c0.7-0.7,0.7-1.7,0-2.4C48.9-0.1,47.9-0.1,47.2,0.6z'/%3E %3C/svg%3E ");
    content: "";
    transform: translateY(-50%);
  }

  &.is-focused { border-color: darken($input-border-color,20%); }

  select {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 2;
    width: 100%; // needed for Firefox
    height: 100%; // needed for IE
    cursor: pointer;
    opacity: 0;
    appearance: none;
  }
}

.form-selected-text-prefix {
  margin-right: 5px;
  font-weight: bold;
}

// ---------------------------------------------------------------- //
// Common Form Classes:

// Wraps each field component
.form-field {
  margin-top: 20px;
  margin-bottom: 25px;
  font-family: $secondary-font;
}

// Form label - applied to the label itself. Prefer to style off titles.
.form-label {
  font-family: $secondary-font;
}

// Applied to labels that are used as option titles
.form-field-title {
  display: block;
  margin-bottom: $gutter / 2;
  text-transform: uppercase;
}

.product-option-title {}
.form-option-title {}

// Applied to labels used as option text (radios/checkboxes)
.form-label-text { user-select: none; }
.product-option-text { user-select: none; }
.form-option-text { user-select: none; }

// inline inputs
.form-field-product-list,
.form-field-checkbox,
.form-field-radio {
  .form-field-control {
    display: inline-block;
    text-transform: uppercase;
  }

  .form-input {
    display: inline-block;
    width: auto;
  }

  .form-radio,
  .form-checkbox {
    @extend .show-for-sr;

    + .form-label-text:before {
      width: 12px;
      height: 12px;
      margin-right: 12px;
      cursor: pointer;
    }
  }

  .form-radio,
  .form-checkbox {
    + .form-label-text:before {
      @extend %unchecked;
    }

    &:checked + .form-label-text:before {
      @extend %checked;
    }
  }
}

// Required form element display 'required' text next title
.required-text {
  font-size: 0;
  color: $white;

  &:before {
    margin-left: -2px;
    font-size: $font-size;
    font-weight: bold;
    color: $error-color;
    content: "*";
  }
}


// ---------------------------------------------------------------- */
// Swatch: Set of options displaying color / pattern choices

.swatch-value {
  font-weight: normal;
  color: lighten($body-color, 25%);

  em {
    font-style: normal;
  }
}

.form-field-swatch {
  .form-field-control { @extend %clearfix; }

  .swatch-wrap {
    position: relative;
    display: block;
    float: left;
    margin-right: -1px;

    .form-label-text { display: none; }

    &:hover {
      .swatch-pattern-expanded {
        z-index: 5000;
        width: 100px;
        opacity: 1;
        visibility: visible;
        transform: translate(-50%, 0);
      }
    }
  }

  // The block that the swatch sits within.
  .swatch {
    position: relative;
    display: block;
    width: 40px;
    height: 40px;
    padding: 3px;
    opacity: 0.3;

    svg {
      width: 28px;
      height: 28px;
      box-shadow: 0 0 0 1px rgba(#000, 0.1) inset;
    }
  }

  // Blocks displaying the pattern / colors
  .swatch-color {
    position: absolute;
    top: 5px;
    right: 5px;
    bottom: 5px;
    left: 5px;
    background: {
      position: 50%;
      repeat: no-repeat;
      size: cover;
    }

    &.secondary {
      left: 50%;
    }

    &.tertiary {
      top: 50%;
      left: 50%;
    }
  }

  .swatch-radio {
    position: absolute;
    top: 0;
    left: 0;
    z-index: 1;
    width: 100%;
    height: 100%;
    cursor: pointer;
    opacity: 0;

    // Unselected Swatch Items
    + .swatch {
      border: 1px solid $input-border-color;
    }

    // Selected Swatch Item
    &:checked + .swatch {
      opacity: 1;
    }
  }

  .swatch-pattern-expanded {
    position: absolute;
    left: calc(100% + 13px);
    top: calc(100% + 5px);
    padding: 3px;
    background-color: $body-background-color;
    border: 1px solid $border-color;
    border-radius: 3px;
    opacity: 0;
    visibility: hidden;
    transition: opacity 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);

    @media only screen and (max-device-width: 768px) { display: none; }
  }

  .swatch-pattern-image {
    display: block;
    width: 100%;
    height: auto;
    border-radius: 3px;
    background: {
      position: 50%;
      repeat: no-repeat;
      size: cover;
    }
  }
}

// ---------------------------------------------------------------- */
// Date Field Components

.form-field-date {
  @extend %clearfix;
  max-width: 280px;

  .form-date-year,
  .form-date-month,
  .form-date-day {
    float: left;
  }

  .form-date-year { width: 38%; }

  .form-date-month {
    width: 31%;
    margin-right: 3%;
    margin-left: 3%;
  }

  .form-date-day { width: 25%; }
}

.product-event-bounds {
  display: inline-block;
  margin-top: 5px;
}


// ---------------------------------------------------------------- */
// Rectangles - Form options displayed as a series of rectangles with text

.form-field-rectangle {
  @extend %clearfix;

  .rectangle {
    position: relative;
    display: inline-block;
    float: left;
    margin-right: -1px;
  }

  .rectangle-text {
    display: block;
    min-width: 40px;
    padding: 10px 14px;
    font-size: $font-size;
    font-weight: bold;
    line-height: 1;
    text-align: center;
    background-color: $white;
    border: 1px solid $input-border-color;
    opacity: 0.3;
  }

  .form-input {
    position: absolute;
    z-index: 1;
    width: 100%;
    height: 100%;
    cursor: pointer;
    opacity: 0;

    // Selected Item
    &:checked + .rectangle-text {
      color: darken($body-color, 25%);
      opacity: 1;
    }
  }
}


// ---------------------------------------------------------------- */
// File

.form-file {
  min-height: 40px;
}

.form-file-types,
.form-file-size {
  display: inline-block;
  margin-top: 10px;
  margin-right: 5px;
}


// ---------------------------------------------------------------- */
// Pick-Product

.product-picklist-radio {
  @extend .show-for-sr;

  + .product-picklist-item-body:before {
    @extend %unchecked;
    position: absolute;
    top: 50%;
    left: 15px;
    width: 12px;
    height: 12px;
    margin-right: 12px;
    cursor: pointer;
    transform: translateY(-50%);
  }

  &:checked + .product-picklist-item-body:before {
    @extend %checked;
  }
}

.product-picklist-item {
  min-width: 350px;
  border-right: 1px solid $border-color;
  border-bottom: 1px solid $border-color;
  border-left: 1px solid $border-color;

  @include breakpoint(s) { min-width: 0; }

  &:first-of-type {
    border-top: 1px solid $border-color;
    border-radius: 3px 3px 0 0;
  }

  &:last-of-type { border-radius: 0 0 3px 3px; }
}

.product-picklist-item-body {
  position: relative;
  z-index: 1;
  display: table;
  width: 100%;
  min-height: $gutter * 3;
  padding: 10px 20px 10px 5px;
  cursor: pointer;
}

.product-picklist-image-wrap {
  position: absolute;
  top: 50%;
  left: 40px;
  display: block;
  width: 100%;
  height: 100%;
  max-width: 40px;
  transform: translateY(-50%);
}

.product-picklist-image {
  position: absolute;
  top: 50%;
  left: 0;
  max-height: 100%;
  padding: 3px;
  transform: translateY(-50%);
}

.product-picklist-label-text {
  display: table-cell;
  padding-left: 90px;
  line-height: 1.4;
  color: inherit;
  vertical-align: middle;

  .picklist-none & { padding-left: 35px; }
}

/* scss-combine:general/sidebar */
//TODO: switch to use revelealer

.facet-option {
  display: none;
  margin-top: 15px;

  &.is-open {
    display: block;
  }
}

.facet-filter {
  padding-top: 25px;
  padding-right: 25px;
  padding-bottom: 25px;
  border-top: 1px solid $border-color;
}

.facet-heading {
  font-size: 20px;
}

.facet-section-heading {
  margin-top: 0;
  margin-bottom: 0;
  font-family: $secondary-font;
  font-size: 13px;
  font-weight: bold;
  text-transform: uppercase;
}

.facet-toggle {
  float: right;
  padding-right: 10px;
  padding-left: 10px;
  cursor: pointer;
}

.facet-select {
  display: inline-block;
  font-family: $secondary-font;
  font-size: 14px;
  color: lighten($body-color, 25%);

  &:before { @extend %unchecked; }
  &.checked:before { @extend %checked; }

  &:hover,
  &:active {
    color: lighten($body-color, 25%);
  }
}

.collection-facet-list-toggle {
  appearance: none;
  padding: 0;
  color: lighten($body-color, 25%);
  background: transparent;
  border: 0;
  outline: 0;
  font-family: $secondary-font;
  font-size: 14px;

  .show-less-text {
    display: none;
  }
}

.facet-range {
  .form-field {
    float: left;
    width: 30%;
    margin: 0 3% 0 0;
  }

  .form-actions {
    display: inline-block;
    width: 30%;
  }

  .min-price, .max-price {
    width: 100%;
    height: 40px;
    padding: 8px 5px;
    text-align: center;
  }

  .button {
    display: block;
    width: 100%;
    height: 40px;
    padding: 3px;
    font-size: 12px;
  }
}

.facet-rating {
  .product-rating {
    display: inline-block;
    text-indent: 0;
  }

  .rating-stars-readonly {
    vertical-align: top;
  }
}

.facet-clear {
  float: right;
  color: lighten($body-color, 25%);
}

/* scss-combine:general/table */
/* Tables */

table {
  min-width: 100%;
  color: $body-color;
  border-color: $border-color;
}

th {
  @extend %h4;
  color: $body-color !important;
  background: lighten($accent-color, 25%);
  border-left: 1px solid $border-color;

  &:first-child {
    border-left: 0;
  }
}

td {
  color: lighten($body-color, 25%);
  text-align: center;
}

table.geotrust-ssl-seal-table {
  min-width: auto;
  margin-top: 35px;
  margin-bottom: 0;
  border: 0;

  td {
    padding: 0;
    border: 0;
  }
}

/* scss-combine:general/tabs */
.tab-content-panel {
  display: none;

  &.active {
    display: block;
  }
}

/* scss-combine:general/rte */
.rte {
  font-size: $font-size;
  color: $body-color;

  h1 { @extend %h1; }
  h2 { @extend %h2; }
  h3 { @extend %h3; }
  h4 { @extend %h4; }
  h5 { @extend %h5; }
  h6 { @extend %h6; }

  ul,
  ol {
    margin-top: 20px;
    margin-bottom: 20px;
    margin-left: 20px;
    line-height: 25px;
  }

  ul {
    list-style: disc;
  }

  blockquote {
    padding-right: 50px;
    padding-left: 40px;
    margin: 60px 0;
    font-size: 26px;
    line-height: 39px;
    border-left: 2px solid $accent-color;
  }

  pre {
    font-size: 12px;
  }

  .fluid-width-video-wrapper {
    margin-top: 40px;
    margin-bottom: 40px;

    @include breakpoint(m-up) {
      width: auto;
      margin-right: -55px;
      margin-left: -55px;
    }
  }

  &.blog-content,
  &.page-content {
    font-size: 16px;
    line-height: 31px;

    &:first-letter {
      float: left;
      padding-top: 5px;
      padding-right: 15px;
      padding-left: 5px;
      font-size: 97px;
      line-height: 73px;
      color: $accent-color;
    }

    ul,
    ol {
      margin-top: 40px;
      margin-bottom: 40px;
      line-height: 31px;
    }
  }
}

.rss-feed-item {
  display: block;
  padding-bottom: 20px;
  margin-bottom: 30px;
  border-bottom: 1px solid $border-color;
}


/* scss-combine:components/alerts */
.alert {
  position: fixed;
  top: $gutter;
  left: 50%;
  z-index: 10002;
  width: 80%;
  max-width: 600px;
  padding: 8px 16px;
  background-color: $input-background-color;
  border: 1px solid $border-color;
  transform: translateX(-50%);

  .product-alert &,
  .account-wrapper &,
  .subscribed &,
  .shipping-errors & {
    position: static;
    width: 100%;
    transform: translate(0);
  }
}

.alert-dismiss {
  position: absolute;
  top: 50%;
  right: 0;
  width: 40px;
  height: 40px;
  font-size: 26px;
  line-height: 40px;
  color: lighten($body-color, 30%);
  text-align: center;
  cursor: pointer;
  transform: translateY(-50%);
}

.alert-dismiss + .alert-message {
  margin-right: 25px;
}

/* scss-combine:components/amp-product-options */
.amp-product-options {
  .form-field-quantity-control {
    position: relative;
    display: block;

    .form-input {
      width: 100%;
      padding-right: 40px;
      padding-left: 40px;
      text-align: center;
    }

    .quantity-control-action {
      position: absolute;
      top: 50%;
      display: flex;
      justify-content: center;
      align-items: center;
      width: 40px;
      height: 40px;
      margin-top: -20px;
      cursor: pointer;

      &:hover {
        color: $accent-color;
      }
    }

    .quantity-decrement {
      left: 8px;
    }

    .quantity-increment {
      right: 8px;
    }
  }

  .button-progress .complete-text {
    top: calc(50% - 10px);
  }
}

/* scss-combine:components/banners */
.banners {
  padding: 0 $gutter * 2;
  background: $banner-background-color;

  .rte {
    font-family: $secondary-font;
    color: $banner-text-color;

    @if lightness($banner-background-color) < 80% {
      @extend %font-smoothing;
    }

    p:first-of-type { margin-top: 0; }
    p:last-of-type { margin-bottom: 0; }
  }
}

.banner {
  padding: $gutter 0;
  border-bottom: 1px solid $border-color;

  &:last-child { border-bottom: 0; }
}

/* scss-combine:components/breadcrumbs */
.breadcrumb-container {
  margin: 40px 10px;
  font-family: $secondary-font;
  font-weight: normal;
  line-height: 2;
  text-align: center;

  @include breakpoint(s) {
    padding-top: 20px;
    padding-bottom: 20px;
    margin: 0;
  }
}

.breadcrumb-item {
  display: inline-block;
  font-size: 13.5px;

  @include breakpoint(s) {
    font-size: 16px;
  }

  &:not(:last-child):after {
    display: inline-block;
    width: 5px;
    height: 9px;
    background-image: url("data:image/svg+xml;charset=utf8,%3Csvg fill='#{escape-hex($navigation-color)}' viewBox='0 0 26 50' version='1.1' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' xml:space='preserve'%3E %3Cpath d='M0.6 47.1l21.7-22.3L0.6 2.9c-0.7-0.7-0.7-1.7 0-2.4 0.7-0.7 1.7-0.7 2.4 0L25.4 23c0.3 0.3 0.5 0.7 0.5 1.1l0 1.1c0 0.4-0.2 0.9-0.5 1.2L3 49.5c-0.7 0.7-1.7 0.7-2.4 0C-0.1 48.8-0.1 47.8 0.6 47.1z'/%3E %3C/svg%3E");
    background-position: center;
    background-repeat: no-repeat;
    content: "";
  }
}

.breadcrumb-link {
  padding: 12px;
  color: $navigation-color;

  &:hover,
  &:active {
    color: $navigation-hover;
  }
}

.breadcrumb-label {
  padding: 12px;
  color: lighten($navigation-color, 50%);
}

/* scss-combine:components/cart-preview */
.cart-preview-wrap {
  position: absolute;
  top: 46px;
  right: 0;
  display: none;
  width: 430px;
  padding: 30px 35px;
  background-color: $body-background-color;
  border: 1px solid $border-color;

  @include breakpoint(xl-up) {
    .main-header:not(.shopping-tools-top) & {
      top: 74px;
    }
  }

  .no-split-menu.logo-right & {
    right: auto;
    left: -60px;
  }

  &:before {
    position: absolute;
    top: -3px;
    right: 65%;
    display: none;
    width: 13px;
    height: 13px;
    background-color: $body-background-color;
    border-top: 1px solid $border-color;
    border-left: 1px solid $border-color;
    content: "";
    transform: rotate(45deg) translateX(-50%);
  }

  &.animating,
  &.visible {
    display: block;

    @include breakpoint(m) {
      display: none;
    }
  }
}

.cart-preview-title {
  padding: 20px;
  margin-bottom: 20px;
  border-bottom: 1px solid $border-color;
}

.cart-preview-list {
  height: auto;
  max-height: 355px;
  overflow-y: auto;
}

.cart-preview-list-item {
  position: relative;
  display: block;
  float: left;
  width: 100%;
  padding-bottom: 20px;
  margin-bottom: 20px;
  border-bottom: 1px solid $border-color;

  &:last-child {
    margin-bottom: 0;
    border-bottom: 0;
  }
}

.cart-preview-list-item-remove {
  position: absolute;
  top: 0;
  right: 0;
  display: block;
  width: 15px;
  height: 15px;
  font-size: 10px;
  font-weight: normal;
  color: $body-background-color;
  text-align: center;
  background-color: $navigation-color;
  border-radius: 50%;

  &:hover,
  &:active {
    color: darken($body-background-color, 20%);
  }
}

.cart-preview-list-item-image-wrap {
  @include span-columns(3);
}

.cart-preview-list-item-image {
  @include maintain-ratio(1 1);
  position: relative;
  max-width: 100%;
  overflow: hidden;

  a {
    @include coverer;
    float: left;
    width: 100%;
    margin: 0 auto;
    overflow: hidden;

    img {
      @include centerer;
      position: relative;
      display: block;
      height: 100%;
      max-width: none;
    }
  }
}

.cart-preview-list-item-details {
  @include span-columns(9);
  padding-left: 20px;
  font-weight: normal;
  text-transform: none;
}

.cart-preview-list-item-brand {
  margin-bottom: 7px;
  font-family: $secondary-font;
  font-size: 13px;
  font-weight: bold;
  text-transform: uppercase;
}

.cart-preview-list-item-name {
  margin-bottom: 5px;
  font-family: $primary-font;
  font-size: 18px;
  line-height: 1.3em;

  > a {
    color: $navigation-color;

    &:hover,
    &:active {
      color: $navigation-hover;
    }
  }
}

.cart-preview-list-options {
  color: lighten($navigation-color, 50%);
}

.cart-preview-list-item-price {
  font-family: $primary-font;
  font-size: 18px;
  line-height: 15px;
  color: lighten($navigation-color, 50%);

  .multiplier {
    font-family: $secondary-font;
    font-weight: bold;
  }
}

.cart-preview-footer {
  .button {
    display: inline-block;
    width: 49%;
    padding-right: 0;
    padding-left: 0;
    float: right;
  }

  .button.secondary {
    @extend %alternate-underline;
    float: left;
  }
}

.cart-preview-taxes,
.cart-preview-discount {
  padding-top: 15px;
  padding-bottom: 15px;
  border-top: 1px solid $border-color;
}

.cart-preview-pricing {
  margin-bottom: 30px;
  border-top: 1px solid $border-color;
  border-bottom: 1px solid $border-color;
}

.cart-preview-total-price {
  font-family: $primary-font;
  font-size: 21px;
  font-weight: normal;
  text-align: right;
  text-transform: none;
}

.cart-preview-empty {
  padding: 40px;
  font-size: $font-size;
  line-height: $font-size;
  text-align: center;
}

/* scss-combine:components/footer */
.site-footer-wrap {
  background-color: $footer-background-color;
}

.site-footer {
  @extend %clearfix;
  @include outer-container($max-content-width);
  padding-top: 110px;
  padding-bottom: 75px;
  font-family: $secondary-font;
  font-size: 16px;
  color: rgba($footer-text-color, 0.78);
  text-transform: uppercase;

  @include breakpoint(l) {
    padding-left: 90px;
    padding-right: 90px;
  }

  @include breakpoint(s) {
    padding-left: 35px;
    padding-right: 35px;
