/* scss-combine:components/header */
.main-header {
  position: relative;
  font-family: $navigation-font;
  font-size: 17px;
  color: $navigation-color;
  background-color: $body-background-color;

  @include breakpoint(m) {
    height: $main-navigation-height;
  }

  @include breakpoint(m-up) {
    height: $main-navigation-height + $secondary-navigation-height;
    padding-right: 45px;
    padding-left: 45px;
    font-weight: $navigation-weight;

    &.no-split-menu {
      height: $main-navigation-height + $main-navigation-height;
    }
  }

  @include breakpoint(xl-up) {
    &:not(.shopping-tools-top) {
      &.split-menu {
        height: $main-navigation-height;
        padding: 45px;
      }
    }

    &.no-split-menu {
      padding-top: 0;
      padding-bottom: 0;
    }
  }
}

.primary-navigation {
  @include breakpoint(m-up) {
    position: absolute;
    top: $secondary-navigation-height;
    left: 0;
    width: 100%;
    height: $main-navigation-height;

    .no-split-menu & {
      margin-top: $secondary-navigation-height;
    }
  }

  @include breakpoint(xl-up) {
    .main-header:not(.shopping-tools-top).split-menu & {
      top: 0;
    }
  }
}

.header-tools {
  @include breakpoint(m-up) {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: $secondary-navigation-height;
    border-bottom: 1px solid $border-color;

    .no-split-menu & {
      height: $main-navigation-height;
      font-size: 15px;
    }
  }

  @include breakpoint(xl-up) {
    .main-header:not(.shopping-tools-top).split-menu & {
      position: static;
      width: auto;
      height: auto;
      border-bottom: 0;
    }
  }
}

.main-menu {
  @include breakpoint(m) {
    position: fixed;
    top: 0;
    bottom: 0;
    z-index: 10000;
    display: none;
    width: 100%;
    min-width: $min-width;
    padding: $gutter * 3 $gutter * 2 $gutter * 2;
    overflow-y: scroll;
    font-size: 30px;
    text-align: center;
    text-transform: uppercase;
    background-color: rgba($mobile-navigation-background, 0.9);

    a {
      color: $mobile-navigation-text;
    }

    &.animating,
    &.visible {
      display: block;
    }

    &.animating {
      opacity: 0;
      transition: opacity 0.25s $ease-out-quad;
    }

    &.visible {
      opacity: 1;
    }
  }

  @include breakpoint(m-up) {
    position: relative;
    top: 50%;
    z-index: 1;
    margin-right: auto;
    margin-left: auto;
    text-align: center;
    transform: translateY(-50%);

    .split-menu & {
      max-width: 850px;
    }
  }
}

.nav-menu-toggle {
  display: none;

  @include breakpoint(m) {
    position: absolute;
    top: 50%;
    left: 40px;
    z-index: 10001;
    display: inline-block;
    width: 24px;
    height: 20px;
    padding: 0;
    background-color: transparent;
    border: 0;
    transform: translateY(-50%);
    color: $navigation-color;

    &:focus {
      outline: none;
    }

    .text {
      position: absolute;
      top: 50%;
      left: 35px;
      text-transform: uppercase;
      transform: translateY(-50%);
    }

    .icon,
    .icon:before,
    .icon:after {
      position: absolute;
      display: block;
      width: 100%;
      height: 4px;
      background-color: $navigation-color;
      border-radius: 9999px;
      content: "";
    }

    .icon {
      top: 50%;
      font-size: 0;
      text-indent: -9999px;
      transform: translateY(-50%);
    }

    .icon:before {
      top: -8px;
      transition-property: top, transform;
    }

    .icon:after {
      bottom: -8px;
      transition-property: bottom, transform;
    }

    .icon:before,
    .icon:after {
      transition-delay: 0.25s, 0s;
      transition-duration: 0.25s, 0.25s;
    }

    &.active {
      .icon {
        background-color: transparent;
      }

      .icon:before,
      .icon:after {
        background-color: $mobile-navigation-text;
        transition-delay: 0s, 0.3s;
      }

      .icon:before {
        top: 0;
        transform: rotate(45deg);
      }

      .icon:after {
        bottom: 0;
        transform: rotate(-45deg);
      }
    }
  }

  @include breakpoint(s) {
    left: 25px;

    .text { display: none; }
  }
}

.nav-menu-toggle:hover:not(.active) {
  color: $navigation-hover;

  .icon,
  .icon:before,
  .icon:after {
    background-color: $navigation-hover;
  }

  &.active {
    .icon {
      background-color: transparent;
    }
  }
}

