  }
}

.previous {
  .pagination-link,
  .pagination-disabled {
    background-image: url("data:image/svg+xml;charset=utf8,%3Csvg fill='#{escape-hex($body-color)}' viewBox='0 0 26 50' version='1.1' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' xml:space='preserve'%3E %3Cpath d='M25.4 47.1L3.7 24.8 25.4 2.9c0.7-0.7 0.7-1.7 0-2.4 -0.7-0.7-1.7-0.7-2.4 0L0.6 23c-0.3 0.3-0.5 0.7-0.5 1.1l0 1.1c0 0.4 0.2 0.9 0.5 1.2l22.4 23c0.7 0.7 1.7 0.7 2.4 0C26.1 48.8 26.1 47.8 25.4 47.1z'/%3E %3C/svg%3E");
  }
