/* scss-combine:components/subscribe */
.site-subscribe {
  padding: 65px 30px;
  background-color: $sign-up-background-color;

  @include breakpoint(s) {
    padding-top: 50px;
    padding-bottom: 50px;
  }

  @include breakpoint(m) {
    padding-top: 55px;
    padding-bottom: 55px;
  }

  @include breakpoint(m-up) {
    padding-top: 65px;
    padding-bottom: 65px;
  }
}

.site-subscribe-summary {
  text-align: center;
}

.subscription-form {
  max-width: $max-content-width;
  margin: 0 auto;
  font-family: $secondary-font;
  font-size: 0;
  text-align: center;

  .form-field {
    @include breakpoint(m) { margin-top: 0; }
  }
}

.subscription-form-submit {
  display: inline-block;
  height: $subscribe-form-height;
  padding-top: 0;
  padding-bottom: 0;
  margin-left: $subscribe-form-button-margin;
  font-size: 16px;
  text-transform: uppercase;
  vertical-align: middle;
  background-color: $sign-up-button-color;
  border-color: $sign-up-button-color;

  @include breakpoint(m) {
    width: $subscribe-submit-width;
    padding-right: 0;
    padding-left: 0;
  }

  @include breakpoint(s) {
    margin-left: 0;
  }
}

.subscription-form-email {

  .form-label {
    display: inline-block;
    font-size: 18px;
    font-weight: bold;
    color: $sign-up-text-color;
    text-transform: uppercase;
    vertical-align: middle;
    cursor: pointer;

    @if lightness($sign-up-text-color) > 80% {
      @extend %font-smoothing;
    }

    @include breakpoint(m) {
      display: block;
      margin-bottom: 35px;
    }

    @include breakpoint(m-up) {
      margin-right: 30px;
    }
  }

  .form-input {
    height: $subscribe-form-height;
    padding-right: 20px;
    padding-left: 20px;
    font-size: 18px;
    text-align: left;
    vertical-align: middle;
    border: 0;

    @include breakpoint(m) {
      width: calc(100% - #{$subscribe-submit-width + $subscribe-form-button-margin});
    }

    @include breakpoint(s) {
      width: calc(100% - #{$subscribe-submit-width});
      font-size: 16px;
    }

    @include breakpoint(m-up) {
      width: 280px;
    }

    @include breakpoint(xl-up) {
      .main-header:not(.shopping-tools-top) & {
        width: 415px;
      }
    }
  }
}

/* scss-combine:components/quantity */
.quantity-text {
  display: block;
  margin-bottom: 5px;
  font-size: 12px;
  font-weight: bold;
  text-transform: uppercase;
}

.form-field-quantity-control {
  position: relative;
  display: inline-block;
  width: 100%;
  max-width: 150px;
  margin-bottom: 5px;
  font-size: 15px;
  vertical-align: top;

  .form-input {
    width: calc(100% - 20px);
    padding: 8px 5px;
    text-align: center;
    -moz-appearance: textfield;

    &::-webkit-outer-spin-button,
    &::-webkit-inner-spin-button {
      -webkit-appearance: none;
    }
  }
}

.quantity-control {
  position: absolute;
  right: 0;
  display: block;
  width: 20px;
  height: 20px;
  font-size: 14px;
  line-height: 20px;
  text-align: center;
  cursor: pointer;
  background-color: $input-background-color;
  border: 1px solid $input-border-color;
  border-left: 0;
  user-select: none;

  &.quantity-increment {
    top: 0;
    border-bottom: 0;
  }

  &.quantity-decrement {
    bottom: 0;
  }
}

.quantity-update {
  font-size: 12px;
  font-weight: bold;
  color: lighten($body-color, 25%);
  cursor: pointer;
}

/* scss-combine:components/quick-shop */
.quick-shop-modal {
  width: 100%;
  max-width: calc(100% - (#{$gutter} * 4));
  opacity: 0;
  transition: opacity 0.2s $ease-out-quad;

  // for fading in once the ajax request has completed
  .modal-wrapper.loaded & { opacity: 1; }

  // Overrides for some product styling
  .product-details,
  .product-info .form { border-bottom: 0; }

  .product-main-image {
    text-align: center;

    .product-image { max-height: 600px; }
  }
}

.quick-shop-trigger {
  @extend %font-smoothing;
  position: absolute;
  top: 50%;
  left: 50%;
  padding: 16px 24px;
  margin: 0;
  font-weight: 400;
  color: $site-background-color;
  text-align: center;
  cursor: pointer;
  background-color: $body-color;
  opacity: 0;
  visibility: hidden;
  transform: translate(-50%, -50%);
  transition: all 0.2s $ease-out-quad;

  @include breakpoint(m) {
    display: none;
  }

  .product-item-thumbnail:hover & {
    opacity: 1;
    visibility: visible;
  }
}

.quick-shop-wrapper {
  position: fixed;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  z-index: 4000;
  visibility: hidden;
  opacity: 0;
  transition: all 0.2s $ease-out-quad;
  @include modal-background(rgba($site-background-color, 0.5));

  &.visible {
    visibility: visible;
    opacity: 1;
  }
}

.quick-shop {
  position: absolute;
  top: 50%;
  left: 50%;
  z-index: 1000;
  max-height: 90%;
  max-width: 1000px;
  width: 100%;
  padding: $gutter * 2;
  background: white;
  font-size: 0;
  overflow-y: scroll;
  opacity: 0;
  transition: opacity 0.2s $ease-out-quad;

  &.active { opacity: 1; }

  .product-images {
    margin-bottom: 0;
  }

  .product-details {
    padding-top: 0;
    padding-left: $gutter * 2;
    margin-top: 0;
    margin-bottom: 0;
  }

  .form-error-message {
    font-size: $font-size;
  }
}

.quick-shop-product-title {
  @extend %heading-text-treatment;
  margin: 0;
  font-size: 26px;
}

.quick-shop-view-product {
  display: block;
  margin-top: 30px;
  font-family: $secondary-font;
  font-size: 14px;
  color: $body-color;
}

/* scss-combine:components/apple-pay */
.apple-pay-checkout-button {
  display: none;
  height: 60px;
  background: {
    position: 50% 50%;
    repeat: no-repeat;
    size: 100% 60%;
  }
  border-radius: 5px;

  @if stencilString("apple_pay_style") == "white" {
    background-color: white;
    background-image: -webkit-named-image(apple-pay-logo-black);
  } @else if stencilString("apple_pay_style") == "white-border" {
    background-color: white;
    background-image: -webkit-named-image(apple-pay-logo-black);
    border: 0.5px solid black;
  } @else {
    background-image: -webkit-named-image(apple-pay-logo-white);
    background-color: black;
  }

  .cart-additional-checkout-buttons & {
    margin-top: 15px;
    margin-bottom: 15px;
  }

  .apple-pay-supported & {
    display: block;
  }
}

