    }
  }
}

.product-grid-gutter {
  width: 0;

  @include breakpoint(m-up) {
    width: flex-gutter($grid-columns);
  }
}

.product-item-link {
  display: block;
}

.product-item-thumbnail {
  position: relative;
}

.product-item-sale {
  position: absolute;
  top: -18px;
  left: -8px;
  display: inline-block;
  width: 63px;
  height: 63px;
  font-family: $secondary-font;
  font-weight: bold;
  line-height: 60px;
  text-transform: uppercase;
  border: 2px solid $accent-color;
  border-radius: 50%;
}

.product-item-compare {
  display: inline-block;
  margin-top: 8px;

  .form-label-text {
    font-family: $secondary-font;
    font-size: 14px;
    text-transform: none;
  }
}

.compare-button {
  display: none;
}

.compare-button-visible {
  display: inline-block;
}

.product-item-title {
  margin-bottom: 0.4em;
  font-size: 20px;
}

.product-item {
  margin-bottom: 50px;
  text-align: center;
  user-select: none;
}

.products-featured {
  .product-item {
    text-align: center;
  }

  .product-item-title {
    font-size: 32px;
  }
}

.section-title {
  .products-related &,
  .products-featured & {
    @extend %h4;
    margin-bottom: 60px;
    text-align: center;
  }
}

.products-featured-wrap {
  @include content-padding;

  overflow: auto;
  background-color: $site-background-color;
}

// Header styles for the home page
.home .products-featured .section-title {
  @extend %h1;
  font-size: 42px;
  text-transform: none;

  @include breakpoint(m) {
    margin-top: 100px;
    margin-bottom: 66px;
  }

  @include breakpoint(s) {
    margin-top: 66px;
    margin-bottom: 55px;
  }

  @include breakpoint(m-up) {
    margin-top: 125px;
    margin-bottom: 66px;
  }

  @include breakpoint(xl-up) {
    margin-top: 185px;
    margin-bottom: 78px;
  }
}

.products-related-wrap {
  @include breakpoint(m) {
    padding-right: 60px;
    padding-left: 60px;
  }

  @include breakpoint(s) {
    padding-right: 30px;
    padding-left: 30px;
  }

  @include breakpoint(m-up) {
    padding-right: 40px;
    padding-left: 40px;
  }
}

.products-related {
  @include outer-container($max-content-width);
}

.product-slide {
  display: none;

  &.active {
    display: block;
  }
}

.form-field-quantity-control .form-input {
  height: 40px;
}
/* scss-combine:products/product-price */
.price {
  font-family: $primary-font;
  color: lighten($body-color, 25%);

  .product-details & {
    margin-top: 8px;
    margin-bottom: 8px;
    font-size: 35px;
  }

  .product-item-details & {
    font-size: 18px;
    color: lighten($body-color, 25%);
  }

  &.call-for-price {
    font-size: 20px;
  }
}

.price-withouttax,
.price-withtax,
.price-saved {
  display: block;
  margin-bottom: 4px;
}

.price-rrp {
  margin-right: $gutter / 2;
  text-decoration: line-through;
}

// Changes colour of sale price only on the single product page
.product-details .price-rrp + .price-value { color: $accent-color; }

.price-value {
  margin-right: $gutter / 2;
}

.price-tax-label {
  margin-left: 2px;
  font-family: $primary-font;
  font-size: 14px;
  color: lighten($body-color, 25%);
}

.price-saved {
  font-size: 20px;
  color: $body-color;
}

// Hides tax labels in the product grid when the store only displays 1 price
.product-item .price {
  .price-tax-label {
    display: none;
  }

  &.multiple-prices .price-tax-label {
    display: inline;
  }
}

// Call for price
.product-call-for-price {
  font-family: $primary-font;
  font-weight: bold;
  color: lighten($body-color, 25%);
}


// Pages

/* scss-combine:pages/blog */
.blog-item {
  padding-bottom: 80px;
  border-bottom: 1px solid $border-color;

  &:last-of-type {
    padding-bottom: 0;
    border-bottom: 0;
  }
}

.blog-post-single {
  margin-bottom: 80px;
}

.post-header {
  @include outer-container($blog-max-width);
  margin-top: 80px;
  margin-bottom: 35px;
}

.post-featured-image-container {
  @include outer-container($blog-max-width);
  margin-top: 35px;
  margin-bottom: 65px;

  > img {
    display: block;
    width: 100%;
    margin-right: auto;
    margin-left: auto;
  }

  .blog-item & {
    @include outer-container($blog-content-width);
  }
}

.post-title {
  margin-bottom: 0;
  font-size: 40px;
  text-align: center;
}

.post-details {
  margin-top: 15px;
  font-family: $secondary-font;
  font-size: 16px;
  color: lighten($body-color, 25%);
  text-align: center;
  text-transform: uppercase;
}

.post-author:before {
  margin-right: 3px;
  content: "\2014";
}

.blog-content {
  @include outer-container($blog-content-width);
}

.post-footer {
  @include outer-container($blog-content-width);
  margin-top: 40px;
}

.read-more {
  @extend %alternate-underline;
  display: inline-block;
  margin-bottom: 10px;
  font-family: $secondary-font;
  color: $body-color;
  text-transform: uppercase;
}

.post-tags {
  margin-top: 10px;
  font-family: $secondary-font;
  text-transform: uppercase;
}

/* scss-combine:pages/brands */
.brands-grid {
  @extend .product-grid;
}

.brand-item {
  @extend .product-item;

  .brand-image-container {
    .brand-link,
    .brand-image {
      display: block;
    }
  }
}

.brand-item-title {
  @extend .product-item-title;
  margin-top: 0;

  .brand-image-container ~ & {
    margin-top: 13px;
  }
}

/* scss-combine:pages/cart */
.cart-content {
  margin-top: 50px;

  .loading {
    background-color: $site-background-color;
  }
}

.cart-product-row {
  @include row();
  padding-top: 40px;
  padding-bottom: 40px;
  border-top: 1px solid $border-color;

  @include breakpoint(l) {
    position: relative;
    text-align: center;
  }
}

.cart-product-figure {
  @include breakpoint(l) {
    img { max-width: 70%; }
  }

  @include breakpoint(l-up) {
    @include span-columns(2);
    @include shift(0.5);
  }
}

.gift-card-image {
  @include breakpoint(l) {
    svg { max-width: 50%; }
  }
}

.cart-product-quantity {
  font-family: $secondary-font;

  @include breakpoint(l) {
    margin-top: $gutter;

    .quantity-clicker { display: inline-block; }
  }

  @include breakpoint(l-up) {
    @include span-columns(1);
  }
}

.cart-product-details {
  @include breakpoint(l-up) {
    @include span-columns(5);
  }
}

.cart-product-price {
  font-size: 18px;

  @include breakpoint(l-up) {
    @include span-columns(2);
    margin-top: 15px;
    font-weight: bold;
    text-align: right;
  }
}

.cart-product-remove {
  @include breakpoint(l) {
    position: absolute;
    top: $gutter;
    right: $gutter;
  }

  @include breakpoint(l-up) {
    @include span-columns(1);
    margin-top: 15px;
    text-align: right;
  }

  > a {
    font-size: 20px;
    color: $body-color;
  }
}

.cart-product-name {
  margin-top: 20px;
  margin-bottom: 0;
  font-size: 18px;

  > a {
    color: $body-color;
  }
}

.cart-price-per {
  font-size: 0.8em;
  color: lighten($body-color, 25%);
}

.cart-product-brand {
  color: lighten($body-color, 25%);
}

.cart-item-options {
  @extend %clearfix;
  margin-top: $gutter / 2;
  font-size: 12px;
  color: lighten($body-color, 25%);

  dt,
  dd { display: inline-block; }

  dd { margin-left: 2px; }

  @include breakpoint(l) {
    dd {
      &:after { content: ","; }
      &:last-of-type:after { content: ""; }
    }
  }

  @include breakpoint(l-up) {
    dt,
    dd { float: left; }

    dt { clear: left; }
  }
}

.cart-gift-wrap {
  .gift-wrap {
    margin-top: $gutter / 2;
    font-size: 11px;

    > dt,
    > dd {
      display: inline-block;
      margin: 0;
    }

    > dt { font-weight: bold; }
    > dd { margin-right: 2px; }
  }
}

.cart-footer {
  @include row();
}

.cart-footer-control {
  @include row();
}

.cart-footer-left {
  @include breakpoint(l-up) {
    @include span-columns(4);
    @include shift(1);
  }
}

.cart-footer-right {
  @include breakpoint(l-up) {
    @include span-columns(4);
    @include shift(2);
    @include omega;
  }
}

.cart-totals-row {
  @include row();
  margin-bottom: $gutter;
}

.cart-totals-label {
  @include span-columns(8);
  font-size: 14px;
}

.cart-totals-value {
  @include span-columns(4);
  @include omega;
  font-size: 15px;
  text-align: right;
}

.order-total,
.order-subtotal {
  padding-top: $gutter;
  font-size: 17px;
  text-align: right;

  .cart-total-price { margin-left: 30px; }
}

.order-total {
  font-size: 21px;
  font-weight: bold;
  border-top: 1px solid $border-color;
}

.form-gift-certificate,
.form-coupon-code {
  @include row();

  .form-input { @include span-columns(9); }

  .button {
    @include span-columns(3);
    @include omega;
    height: 40px;
    min-width: 0;
    padding: 0;
    line-height: 40px;
  }
}

.cart-coupon-codes,
.cart-gift-certificates {
  margin-bottom: 5px;

  .cart-code-remove {
    margin-left: 3px;
    font-size: 12px;
  }
}

.form-shipping {
  display: none;
  padding-bottom: $gutter;
  margin-top: $gutter;
  margin-bottom: $gutter;
  border-top: 1px solid $border-color;

  .visible & {
    display: block;
  }

  .label-state { @include span-columns(7); }
  .label-zip {
    @include span-columns(5);
    @include omega;
  }

  .form-field { @include row(); }

  .form-label-text {
    font-family: $secondary-font;
    font-size: 14px;

    .ups-shipping-badge {
      margin-top: -2px;
      margin-right: 3px;
      vertical-align: middle;
    }
  }

  .form-select-wrapper {
    max-width: none;
  }

  .button {
    float: right;
    padding: 10px 15px;
    margin-top: $gutter;
  }
}

.shipping-toggle {
  float: right;
  color: $accent-color;
  cursor: pointer;
}

// Cart totals from capacity

.checkout-row {
  @include row;
  margin-top: 60px;
  margin-bottom: 100px;
}

.cart-checkout-buttons,
.cart-additional-checkout-buttons {
  width: 100%;
  float: right;
  clear: both;
  text-align: center;

  @include breakpoint(m-up) {
    @include span-columns(3);
    @include shift(9);
  }

  @include breakpoint(l-up) {
    @include span-columns(3);
    @include shift(8);
  }

  .checkout-button,
  > div {
    width: 100%;

    @include breakpoint(l-up) {
      float: right;
      max-width: 300px;
      text-align: center;
    }
  }
}

// multiple shipping address
.checkout-multiple {
  @extend %alternate-underline;
  display: inline-block;
  margin-top: 10px;
  text-align: center;
}

// workaround to override funky formatting of paypal button
.cart-additional-checkout-buttons {
  @extend %clearfix;
  text-align: center;

  // scss-lint:disable ImportantRule, QualifyingElement
  p { float: none !important; }

  form { clear: both;}
}

.empty-cart {
  text-align: center;
}

// Shipping Calculator

.shipping-quotes {
  clear: both;

  .button {
    padding-top: 10px;
    padding-bottom: 10px;
    margin-top: 10px;
  }
}

.shipping-errors .alert-error { margin-top: $gutter; }

// Cart Discount
.price-discounted {
  text-decoration: line-through;
}

/* scss-combine:pages/collection */
.sidebar {
  @include breakpoint(m-up) {
    @include span-columns(3);
  }

  @include breakpoint(m) {
    @include span-columns(12);
  }

  &.sidebar-disabled { display: none; }
}

.collection-products {
  @include span-columns(9);

  .sidebar-disabled + & {
    @include breakpoint(m-up) {
      @include span-columns(12);
    }
  }

  @include breakpoint(m) {
    @include span-columns(12);
  }
}

.collection-product-listing-header {
  @extend %clearfix;
  margin-bottom: 30px;
  border-top: 1px solid $border-color;

  .sorting-form {
    float: right;

    .form-select-wrapper {
      display: inline-block;
      width: 175px;
    }

    .form-label {
      display: inline-block;
      margin-right: 15px;
      font-family: $secondary-font;
      font-size: 13px;
      font-weight: bold;
      text-transform: uppercase;
    }
  }
}

.compare-form {
  position: fixed;
  right: 10px;
  bottom: 0;
  z-index: 1999;
  box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);

  .form-field {
    padding: 0;
    margin: 0;
    background-color: $white;
  }
}

.compare-button {
  display: none;
  height: 45px;
  padding-right: 15px;
  font-family: $secondary-font;
  font-size: 13px;
  font-weight: bold;
  line-height: 40px;
  color: $body-color;
  text-transform: uppercase;
  background-color: transparent;
  border-width: 0;

  &:focus {
    outline: none;
  }
}

.compare-button-visible {
  display: inline-block;
}

.compare-count {
  display: inline-block;
  min-width: 20px;
  margin-right: 10px;
  margin-left: 15px;
  font-family: $secondary-font;
  font-size: 13px;
  font-weight: 700;
  color: $white;
  text-align: center;
  text-transform: uppercase;
  background-color: $accent-color;
  border-radius: 9999em;

  &:empty { display: none; }
}

.collection-product-image {
  display: block;
  margin: 0 auto;
}
/* scss-combine:pages/compare-products */
.compare-heading {
  width: 25%;
  padding-left: 0;
  margin-bottom: 40px;
  font-size: 18px;
  font-weight: normal;
  vertical-align: top;
}

.compare-table {
  border-right: 0;
  border-bottom: 0;
  border-left: 0;

  th,
  td {
    background-color: transparent;
    border-top: 1px solid $border-color;
