/* scss-combine:pages/contact */
.contact-us {
  .form { @extend %clearfix; }

  .form-field {
    @include span-columns(6);

    &:nth-of-type(even) { @include omega; }

    .form-input { max-width: none; }
  }

  .form-field-multiline { @include span-columns(12); }

  .form-actions {
    @include span-columns(12);
    margin-top: $gutter;
  }

  .g-recaptcha {
