}

/* scss-combine:pages/product */
.product-primary-wrapper {
  @include content-padding;
  background-color: $site-background-color;
}

.product-primary {
  @include outer-container($max-content-width);
}

.product-images,
.product-details {
  @include breakpoint(m) {
