  }

  @include breakpoint(m-up) {
    @include span-columns(6);
  }
}

.product-images {
  @include breakpoint(m) {
    margin-bottom: 55px;
  }

  @include breakpoint(s) {
    margin-bottom: 30px;
  }

  @include breakpoint(m-up) {
    margin-bottom: 95px;
  }
}

.product-main-image {
