.product-video-wrapper {
  margin-bottom: 50px;

  &:last-child {
    margin-bottom: 0;
  }
}

.products-related {
  padding-top: 65px;
  border-top: 1px solid $border-color;
}

/* scss-combine:pages/search */
.search-results-suggestion {
  margin-bottom: 20px;
  font-family: $secondary-font;
  color: lighten($navigation-color, 50%);
  text-align: center;

  a {
    color: inherit;
  }
}

.tab-search-toggles {
  display: block;
  margin-bottom: 40px;
  text-align: center;
}

.tab-title {
  display: inline-block;
  padding-bottom: 15px;
  font-weight: 600;
  cursor: pointer;
  background: none;
  border: 0;
  border-radius: 0;
  outline: none;
  appearance: none;

  a {
    color: inherit;
  }

  &:only-child {
    margin-right: 0;
    margin-left: 0;
  }

  &:first-child {
    margin-right: 20px;
  }

  &:last-child {
    margin-left: 20px;
  }

  &.active {
    color: $body-color;
    border-bottom: 1px solid $body-color;
  }

  &.tab-empty {
    display: none;
  }
}

.brand-category-results {
  font-family: $secondary-font;

  ul {
    margin-top: 15px;
  }
}

.search-results-title {
  margin-top: 20px;
  margin-bottom: 0;
  font-family: $secondary-font;
  font-weight: bold;
  text-transform: uppercase;
}

.content-panel {
  border-top: 1px solid $border-color;
}

.content-listing {
  @extend .page-content;
  margin-top: 20px;
}

.content-result {
  margin-bottom: 40px;

  &:last-child {
    margin-bottom: 0;
  }
}


// Advanced Search
.advanced-search-toggle {
  display: block;
  margin-bottom: 20px;
  font-family: $secondary-font;
  font-weight: bold;
  color: $accent-color;
  text-align: center;
  text-transform: uppercase;
  cursor: pointer;

  &:hover {
    color: $body-color;
  }
}

.advanced-search-panel {
  margin-bottom: 40px;
  border-bottom: 1px solid $border-color;
}

.dropdown {
  position: relative;
}

.dropdown-panel {
  top: 100%;
  left: 0;
  display: none;
  min-width: 100%;
  padding: 21px 10px 20px;

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in,
  &.animating-out {
    opacity: 0;
    transform: translateY(-10px);
    transition: opacity 0.5s ease, transform 0.3s ease;
  }

  &.visible {
    opacity: 1;
    transform: translateY(0);
  }
}

.advanced-search-form {
  .form-field-title {
    font-family: $secondary-font;
  }

  .form-input,
  .form-select-wrapper {
    width: 100%;
    max-width: 100%;
  }

  .form-actions {
    text-align: center;
  }
}

.categories-group {
  background: $input-background-color;
  border: 1px solid $input-border-color;
}

/* scss-combine:pages/sitemap */
.sitemap-content {
  @include row;
  margin-top: 50px;
}

.sitemap-group {
  @include span-columns(4);
  &:nth-child(3n) { @include omega; }
  &:nth-child(4n) { clear: left; }

  @include breakpoint(m) {
    @include span-columns(12);
    margin-top: $gutter * 2;
    &:first-child { margin-top: 0; }
  }

  ul {
    ul {
      margin-bottom: $gutter / 2;
      margin-left: $gutter / 2;
    }
  }

  li {
    margin: $gutter / 4 0;
  }

  a {
    color: inherit;
  }
}

.sitemap-title {
  @extend %h5;
}

.sitemap-showall {
  display: block;
  margin-top: 15px;
  font-weight: 600;
}

/* scss-combine:pages/subscribed */
.subscribed {
  margin-bottom: 60px;

  @include breakpoint(m-up) {
    margin-bottom: 100px;
  }

  .page-content {
    text-align: center;
  }
}


// Module overrides
/* scss-combine:modules/carousel */
.carousel {
  position: relative;
  overflow: hidden;

  @include breakpoint(m) {
    // scss-lint:disable ImportantRule
    height: auto !important;
  }
}

.carousel-item {
  display: none;

  @include breakpoint(m) {
    position: relative;
  }

  &.animating {
    display: block;
    transition: all 0.8s $ease-in-out-quad;
  }

  &.animating-out {
    @include breakpoint(m) {
      position: absolute;
    }
  }

  &.animating-left {
    transform: translateX(100%);

    &.animating-out {
      transform: translateX(-100%);
    }
  }

  &.animating-right {
    transform: translateX(-100%);

    &.animating-out {
      transform: translateX(100%);
    }
  }

  &.visible {
    &,
    &.animating-left,
    &.animating-right {
      display: block;
      transform: translateX(0);
    }
  }
}

.carousel-item-image {
  width: 100%;
}

.carousel-item-info {
  width: 100%;
  text-align: center;

  @include breakpoint(m-up) {
    @include centerer;
  }

  @include breakpoint(m) {
    position: static;
    padding: 70px 40px 80px;
  }

  @include breakpoint(s) {
    padding: 40px 25px 45px;
  }

  .carousel-heading,
  .carousel-caption,
  .carousel-button {
    @include breakpoint(m-up) {
      color: $carousel-text-color;
    }

    @include breakpoint(m) {
      color: $body-color;
    }
  }
}

.carousel-heading {
  margin-top: 0;
  font-family: $primary-font;
  text-transform: none;

  @include breakpoint(m-up) {
    margin-bottom: 22px;
    font-size: 68px;
  }

  @include breakpoint(xl-up) {
    margin-bottom: 25px;
    font-size: 92px;
  }

  @include breakpoint(m) {
    margin-bottom: 20px;
    font-size: 58px;
  }

  @include breakpoint(s) {
    margin-bottom: 15px;
    font-size: 40px;
  }
}

.carousel-caption {
  font-family: $primary-font;

  @include breakpoint(m-up) {
    margin: 15px 0;
    font-size: 30px;
  }

  @include breakpoint(xl-up) {
    margin: 20px 0;
    font-size: 40px;
  }

  @include breakpoint(m) {
    font-size: 24px;
  }

  @include breakpoint(s) {
    margin: 15px 0;
  }
}

.carousel-button {
  @include underline();

  @include breakpoint(m-up) {
    font-size: 21px;
  }

  @include breakpoint(xl-up) {
    font-size: 23px;
  }

  @include breakpoint(m) {
    font-size: 19px;
  }
}

.carousel-navigation-item {
  &.carousel-previous {
    @include breakpoint(m-up) {
      left: 55px;
    }

    @include breakpoint(xl-up) {
      left: 100px;
    }

    @include breakpoint(m) {
      left: 40px;
    }

    @include breakpoint(s) {
      left: 25px;
    }
  }

  &.carousel-next {
    @include breakpoint(m-up) {
      right: 55px;
    }

    @include breakpoint(xl-up) {
      right: 100px;
    }

    @include breakpoint(m) {
      right: 40px;
    }

    @include breakpoint(s) {
      right: 25px;
    }
  }
}

/* scss-combine:modules/account */
// Variables

$account-light-color: $input-background-color;
$account-accent-color: $accent-color;
$account-border-color: $border-color;
$account-gutter: $gutter;
$account-font-size: $font-size;

/* scss-combine:../core/account */
// Default Variables

$account-link-color: #888 !default;
$account-light-color: #fff !default;
$account-accent-color: #5dc5e3 !default;
$account-border-color: #e4e4e4 !default;
$account-gutter: 30px !default;
$account-font-size: 15px !default;
$account-border-radius: 0 !default;
$account-text-color: #5f5f5f !default;

$account-card-background-color: #f5f5f5 !default;

$giftcard-nav-use-default-styles: true !default;

$account-breakpoint-x-small: 400px !default; // the account table columns change to full-width
$account-breakpoint-small: 620px !default; // menu changes from full-width to column
$account-breakpoint-medium: 900px !default; // menu changes from 35% to 25% width

// Internal Variables

$account-font-size-heading: $account-font-size + 2 !default;

$account-text-color-light: lighten($account-text-color,20%) !default;

$account-gutter-inner: $account-gutter / 2 !default;

$auth-gutter: $account-gutter !default;
$auth-font-size: $account-font-size !default;

// Imports

/* scss-combine:account/components/layout */
.account-wrapper {
  @extend %clearfix;
  font-size: $account-font-size;
}

.account-nav,
.account-body {
  vertical-align: top;
}

.account-body {
  @media screen and (min-width: $account-breakpoint-small) {
    float: right;
    width: 65%;
    padding-left: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-medium) {
    width: 75%;
  }

  > :first-child {
    margin-top: 0;
  }

  .account-nav-hidden & {
    float: none;
    width: 100%;
    padding-left: 0;
    margin-left: 0;
  }
}

.account-nav {
  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    float: left;
    width: 35%;
    padding-right: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-medium) {
    width: 25%;
  }
}

/* scss-combine:account/components/forms */
.account-wrapper {
  .form-label-text {
    display: inline-block;
    width: 100%;
    margin-bottom: 5px;
  }

  .form-field-checkbox,
  .form-field-radio {
    .form-label-text { width: auto; }
  }

  .form-field {
    margin-bottom: $account-gutter-inner;
  }

  .form-input {
    width: 100%;

    &.account-form-half {
      width: 50%;
    }
  }

  .form-actions {
    clear: both;
  }
}

.account-form-column-container {
  @extend %clearfix;

  .form-field {
    @media screen and (min-width: 480px) {
      float: left;
      width: 50%;

      &:nth-child(odd) { padding-right: $account-gutter-inner; }
      &:nth-child(even) { padding-left: $account-gutter-inner; }
    }
  }
}

/* scss-combine:account/components/table */
.account-wrapper {
  table {
    width: 100%;
    margin-top: $account-gutter-inner;
    margin-bottom: $account-gutter-inner;
  }

  td,
  th {
    padding: $account-gutter-inner;
  }
}

.account-table-tight {
  th,
  td {
    padding: 10px 0;
    border-bottom: 1px solid $account-border-color;
  }
}

/* scss-combine:account/components/list */
// Layout
// Each list item has 3 sections: thumbnail, body, and badge.

.account-items {
  margin-bottom: $account-gutter;
}

.account-item {
  position: relative;
  display: table;
  width: 100%;
  border-bottom: 1px solid $account-border-color;

  &:first-of-type {
    border-top: 1px solid $account-border-color;
  }
}

.account-item-thumbnail,
.account-item-body,
.account-item-badge,
.account-item-actions {
  padding-top: $account-gutter-inner;
  padding-bottom: $account-gutter-inner;

  @media screen and (min-width: $account-breakpoint-x-small) {
    display: table-cell;
    vertical-align: top;
  }
}

// Link
// Makes the entire item clickable

.account-item-link {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 100;
}

// Thumbnail

.account-item-thumbnail {
  min-width: 100px;

  img {
    height: auto;
    max-width: 100px;
  }
}

// Body

.account-item-body {
  width: 100%;

  @media screen and (min-width: $account-breakpoint-x-small) {
    .account-item-thumbnail + & {
      padding-left: $account-gutter-inner;
    }
  }
}

.account-item-title {
  margin-top: 3px;
  font-size: $account-font-size;

  a {
    color: inherit;
  }
}

.account-item-title-large {
  font-size: $account-font-size * 1.2;
}

.account-item-description {
  margin-top: 0;
  margin-bottom: 5px;
  color: $account-text-color;
}

.account-item-details {
  @extend %clearfix;
  font-size: $account-font-size;
  color: $account-text-color;
}

.account-item-detail {
  float: left;
  margin-top: $account-gutter-inner;
  margin-right: $account-gutter;
}

//scss-lint:disable VariableForProperty
.account-item-detail-title {
  display: block;
  margin-top: 0;
  margin-bottom: 3px;
  font-size: 12px;
  font-weight: normal;
  letter-spacing: 1px;
  color: mix($account-light-color,$account-text-color,60%);
  text-transform: uppercase;
}

// Badge

.account-item-badge,
.account-item-actions {
  white-space: nowrap;
}

/* scss-combine:account/components/header */
.account-header {
  display: table;
  width: 100%;
  margin-bottom: $account-gutter-inner;
}

.account-heading,
.account-toolbar {
  display: table-cell;
  vertical-align: baseline;
}

.account-toolbar {
  text-align: right;

  * {
    display: inline;
  }
}

.account-tool {
  position: relative;
  z-index: 10;
  display: inline-block;
  padding: 0;
  background: transparent;
  margin-left: 10px;
  cursor: pointer;
  color: inherit;
  border: 0;

  &:hover { color: $account-link-color; }

  &:focus { outline: 0; }
}

/* scss-combine:account/components/navigation */
.account-nav-list {
  padding: 0;
  margin-bottom: $account-gutter-inner;
  list-style: none;
  border: 1px solid $account-border-color;
  border-radius: $account-border-radius;
}

.account-nav-item {
  color: $account-link-color;
  border-top: 1px solid $account-border-color;

  &:first-child {
    border-top: 0;
  }

  &.is-active {
    color: $account-accent-color;
    border-left: 3px solid $account-accent-color;
  }
}

.account-nav-link {
  display: block;
  padding: 15px;
  color: inherit;
}

.account-nav-badge {
  float: right;
}

/* scss-combine:account/components/pagination */
.account-wrapper .pagination {
  margin-top: $account-gutter-inner;
}

/* scss-combine:account/components/card */
%account-card-style {
  background: $account-card-background-color;

  .form-input {
    background-color: $account-light-color;
  }
}

.account-card {
  @extend %account-card-style;
  padding: $account-gutter-inner;
  margin: $account-gutter-inner 0;
  border-radius: $account-border-radius;
}

/* scss-combine:account/components/toggle */
// Set the initial toggle state to hidden
.account-toggle-hidden {
  display: none;
}

/* scss-combine:account/components/status */
// Status
// A label used to mark the status of an item

.account-status {
  padding: 6px 10px 5px;
  margin: 0;
  font-size: 12px;
  font-weight: normal;
  line-height: 1;
  letter-spacing: 1px;
  color: $account-light-color;
  text-align: center;
  text-transform: uppercase;
  background: #8e8e8e; // TODO: Colors for each status
}

/* scss-combine:account/components/shipments */
// Shipments
// A list of shipments made for an order. Shown on the Order Details page.

.account-shipments {
  margin-bottom: $account-gutter;
}

.account-shipments-table {
  th,
  td {
    padding: 10px 0;
    text-align: left;
  }

  th {
    padding: $account-gutter-inner 0 0;
    border-top: 1px solid $account-border-color;
  }

  // Override default margins
  .account-wrapper & {
    margin-top: 0;
    margin-bottom: 0;
  }
}


/* scss-combine:account/pages/address */
// Address List

.account-page-add-address {
  .account-content {
    padding-top: $account-gutter-inner;
    border-top: 1px solid $account-border-color;
  }
}

.account-address-detail {
  display: block;
}

// Add Address

.account-address-form {
  padding-top: $account-gutter-inner;

  @media screen and (min-width: $account-breakpoint-medium) {
    .form-field {
      float: left;
      width: 50%;
      padding-right: $account-gutter-inner;

      &:nth-of-type(2n) {
        padding-right: 0;
        padding-left: $account-gutter-inner;
      }

      &:nth-of-type(2n + 1){
        clear: left;
      }
    }
  }

  .form-actions {
    padding-top: $account-gutter-inner;
  }
}

/* scss-combine:account/pages/orders-receipt */
.account-page-orders-receipt .account-content {
  padding-top: $account-gutter-inner;
  border-top: 1px solid $account-border-color;
}

// Summary

.account-receipt-summaries {
  @extend %clearfix;
}

.account-receipt-summary {
  float: left;
  width: 33%;
  padding-right: $account-gutter;
}

.account-receipt-summary-title {
  margin-top: 5px;
  margin-bottom: 10px;
  font-size: $account-font-size;
}

.account-receipt-summary-address {
  line-height: 1.5;
}

.account-receipt-detail {
  margin-bottom: 5px;
}

.account-receipt-detail-title {
  color: $account-text-color-light;
}

// Comments

.account-receipt-comments {
  white-space: pre-line;
}

// Receipt Items (products)

.account-receipt-items {
  padding-top: $account-gutter-inner;
}

.account-receipt-download {
  margin: 10px 0;
}

// Totals

.account-receipt-totals {
  display: table;
  width: 50%;
  margin-left: 50%;
}

.account-receipt-total {
  display: table-row;
  margin-bottom: $account-gutter-inner;
}

.account-receipt-total-title,
.account-receipt-total-value {
  display: table-cell;
  padding-top: $account-gutter-inner;
  padding-bottom: $account-gutter-inner;
  border-top: 1px solid $account-border-color;

  .account-receipt-total:first-of-type & {
    border-top: 0;
  }
}

.account-receipt-total-title {
  font-weight: normal;
  letter-spacing: 1px;
  color: $account-text-color-light;
}

.account-receipt-total-value {
  text-align: right;
}

/* scss-combine:account/pages/recent-items */

/* scss-combine:account/pages/returns */
// Return form

.account-return-table {
  th {
    text-align: left;

    &:last-of-type {
      text-align: right;
    }
  }

  .form-field {
    margin-bottom: 0;
  }
}

.account-return-fields {
  margin-top: $account-gutter;

  .form-field-select {
    width: 50%;
  }
}

/* scss-combine:account/pages/wishlists */
.account-wishlists {
  margin-bottom: 30px;
}

/* scss-combine:account/pages/messages */
// Listing

.account-messages-table.table-clean {
  border-bottom: 1px solid $account-border-color;

  td {
    padding-right: 0;
    padding-left: 0;
    vertical-align: top;
    border-top: 1px solid $account-border-color;
  }
}

.account-messages-table-date {
  width: 22%;
  text-align: right;
}

.account-message-subject {
  margin-top: 0;
  margin-bottom: 10px;
  font-size: $account-font-size-heading;
  font-weight: 500;

  .account-message-is-reply &:before {
    padding-right: 5px;
    content: "\21c9";
  }

  .account-message-is-read & {
    color: $account-text-color-light;
  }
}

.account-message-content {
  padding-right: $account-gutter;
  font-size: $account-font-size;
  line-height: 1.5;
  color: $account-text-color;
  white-space: pre-line;

  .account-message-is-read & {
    color: lighten($account-text-color-light,10%);
  }
}

.account-message-date {
  color: $account-text-color-light;
}

// Form

.account-messages-form-subject {
  width: 50%;
}

/* scss-combine:account/pages/download */
// List

.account-download {
  padding: $account-gutter-inner 0 0;
  margin: $account-gutter-inner 0;
  border-top: 1px solid $account-border-color;
}

.account-download-title {
  margin: 0;
  font-size: 1em;
}

.account-download-link {
  color: inherit;
  text-decoration: underline;
}

.account-download-filename {
  font-size: 1.2em;
}

.account-download-filesize {
  padding-left: 5px;
  font-weight: normal;
  color: $account-text-color-light;
}

.account-download-description {
  padding-top: 10px;
  padding-bottom: 10px;
}

.account-download-expiry {
  font-size: 0.9em;
  color: $account-text-color-light;
}

.account-download-expiry-item {
  display: block;
  padding-bottom: 5px;

  &:last-of-type {
    padding-bottom: 0;
  }
}

// Gallery

.account-downloads-gallery-image {
  text-align: center;
}

.account-downloads-gallery-thumbs {
  @extend %clearfix;
  text-align: center;
}

.account-downloads-gallery-thumb {
  display: inline-block;
  width: 10%;
  height: 10%;
  padding: 0 2px;
  vertical-align: middle;
  cursor: pointer;

  img {
    width: 100%;
    height: auto;
  }
}

/* scss-combine:account/pages/invoice */
.account-invoice-date {
  margin-top: -20px;
  font-size: $account-font-size;
}

// Address

.account-invoice-addresses {
  @extend %clearfix;
  margin-top: $account-gutter;
  margin-bottom: $account-gutter;
}

.account-invoice-address {
  float: left;
  margin-right: $account-gutter;
}

.account-invoice-address-title {
  margin-top: 5px;
  margin-bottom: 10px;
  font-size: $account-font-size;
}

.account-invoice-address-body {
  line-height: 1.5;
}

// List

.account-invoice-table {
  .account-wrapper & {
    margin-bottom: 0;
  }

  th,
  td {
    padding-right: $account-gutter-inner;
    text-align: left;
    vertical-align: top;

    &:nth-of-type(0n+3) {
      width: 10%;
    }

    &:last-of-type {
      width: 160px;
      padding-right: 0;
      text-align: right;
    }
  }

  td:first-of-type {
    width: 60px + 25px;
  }
}

.account-invoice-item-thumbnail {
  width: 60px;
  height: auto;
}

.account-invoice-item-name {
  margin-bottom: 5px;
  font-weight: bold;
}

.account-invoice-total-title {
  font-weight: bold;
}

// Totals

.account-invoice-totals {
  .account-wrapper & {
    width: auto;
    margin-top: 0;
    margin-left: auto;
  }

  th,
  td {
    padding-right: $account-gutter-inner;

    &:last-of-type {
      width: 160px;
      padding-right: 0;
      text-align: right;
    }
  }
}

/* scss-combine:account/pages/settings */
.account-page-settings {
  .alert:first-of-type {
    margin-top: 0;
  }

  .account-content {
    padding-top: $account-gutter-inner;
    border-top: 1px solid $account-border-color;
  }
}

.account-settings-form {
  @extend %clearfix;
  padding-top: $account-gutter-inner;

  .form-field {
    float: left;
    width: 50%;
    padding-right: $account-gutter-inner / 2;
    vertical-align: top;

    &:nth-of-type(2n+0) {
      padding-right: 0;
      padding-left: $account-gutter-inner / 2;
    }
  }

  .form-actions {
    padding-top: $account-gutter-inner;
  }
}


/* scss-combine:account/pages/auth-login */

.account-page-login .alert {
  max-width: 860px;
  margin-top: 0;
  margin-right: auto;
  margin-bottom: $account-gutter;
  margin-left: auto;
}

.account-login-wrapper {
  max-width: 860px;
  margin-right: auto;
  margin-left: auto;

  @media screen and (min-width: $account-breakpoint-small) {
    display: table;
    width: 100%;
    table-layout: fixed;
  }
}

.account-page-login .account-sub-heading {
  margin: 0 0 20px;
}

.account-login,
.account-create {
  @media screen and (min-width: $account-breakpoint-small) {
    display: table-cell;
    vertical-align: top;
  }
}

// Login Form

.account-login {
  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    padding: $account-gutter-inner (30px + $account-gutter-inner) $account-gutter-inner 0;
  }
}

.account-login-button {
  margin-right: $account-gutter-inner;
}

// New Account

.account-create {
  padding: $account-gutter-inner;
  background: $account-card-background-color;
}

.account-create-intro {
  &:empty {
    display: none;
  }
}

.account-create-facts {
  padding-left: 20px;
  margin-bottom: 30px;
  list-style: disc;

  li:empty {
    display: none;
  }
}

/* scss-combine:account/pages/auth-forgot */
.account-page-forgot .account-body {
  width: 100%;
  max-width: 600px;
  margin-right: auto;
  margin-left: auto;
  text-align: center;
  float: none;
}

.account-forgot-form {
  display: table;
  width: 100%;
  margin-top: $account-gutter;

  .form-field,
  .form-actions {
    display: table-cell;
    text-align: left;
    vertical-align: bottom;
  }

  .form-actions {
    width: 1px;
    padding-left: 10px;
  }
}

/* scss-combine:account/pages/auth-create */
.account-page-create {
  .account-header {
    padding-bottom: $account-gutter-inner;
    margin-bottom: $account-gutter;
    border-bottom: 1px solid $account-border-color;
  }

  .account-body {
    max-width: 860px;
    margin-right: auto;
    margin-left: auto;
  }
}

.account-create-form {
  .g-recaptcha > div {
    margin: 0 auto;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    .form-field {
      float: left;
      width: 50%;
      padding-right: $account-gutter-inner;

      &:nth-of-type(2n) {
        padding-right: 0;
        padding-left: $account-gutter-inner;
      }

      &:nth-of-type(2n + 1) {
        clear: left;
      }
    }
  }
}

.account-create-details,
.account-create-address {
  @extend %clearfix;
  padding-bottom: $account-gutter-inner;
  margin-bottom: $account-gutter;
  border-bottom: 1px solid $account-border-color;
}

.account-create-form-actions {
  text-align: center;
}

.account-create-captcha {
  display: inline-block;
  vertical-align: middle;

  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    margin-right: $account-gutter-inner;
  }
}

/* scss-combine:account/pages/auth-created */
.account-page-created .account-body {
  float: none;
  width: 100%;
  max-width: 600px;
  margin-right: auto;
  margin-left: auto;
  text-align: center;

  p {
    margin-bottom: $account-gutter;
    line-height: 1.5;
  }

  em {
    font-family: monospace;
    font-style: normal;
    font-weight: bold;
    background: $account-card-background-color;
  }
}

/* scss-combine:account/pages/auth-new-password */
.account-page-new-password .account-content {
  width: 100%;
  max-width: 400px;
  margin-right: auto;
  margin-left: auto;
}

.new-password-requirements {
  margin-bottom: $account-gutter;
  text-align: center;
}


/* scss-combine:account/pages/gift-certificate */
// (optional) gift card nav

@if ($giftcard-nav-use-default-styles) {
  .giftcard-nav {
    padding: $account-gutter * 2;
    margin-bottom: $account-gutter * 2;
    text-align: center;
  }

  .giftcard-nav-item {
    display: inline-block;
    margin: 0 1em;
    color: $account-link-color;

    &.is-active { color: $account-text-color; }
  }
}

.giftcard-heading-container {
  margin-bottom: $account-gutter * 2;
  text-align: center;
}


// Purchase

.giftcard-form-help-message {
  display: block;
  margin-top: 6px;
}

.giftcard-form-preview {
  iframe { width: 100%; }
}

.giftcard-single-theme {
  display: none;
}


// Check balance

.giftcard-balance-container {
  max-width: 600px;
  margin: 0 auto;
}

.form-giftcard-balance {
  position: relative;

  .form-field-title {
    position: absolute;
    left: -9999px;
  }

  .form-field-control {
    display: block;

    @media screen and (min-width: 600px + ($account-gutter-inner * 2)) {
      margin-right: calc(30% + #{$account-gutter-inner});
    }
  }

  .form-actions {
    @media screen and (min-width: 600px + ($account-gutter-inner * 2)) {
      position: absolute;
      top: 0;
      right: 0;
      width: 30%;
    }
  }

  .button {
    width: 100%;
  }
}


// Redeem

.giftcard-redeem-steps {
  line-height: 1.5;

  li { margin-bottom: 0.75em; }
}



.account-heading {
  @extend .page-heading;
  margin: 0;
}

.auth-sub-heading {
  @extend %h4;
}

.account-wrapper {

  .form-label-text {
    text-transform: none;
  }

  .form-input {
    max-width: none;
    text-align: left;
  }
}

.auth-button-alt {
  @extend .button;
  @extend .inverted;
}

.account-button-secondary {
  @extend .secondary;
}

.account-button-primary,
.account-button-secondary {
  padding: 12px 15px;
  font-size: $font-size - 1;

  .form-actions & {
    height: 40px;
    padding-top: 0;
    padding-bottom: 0;
    line-height: 40px;
  }
}

.account-nav-list {
  background-color: $body-background-color;
}

.account-messages-table td {
  text-align: left;
}

.account-item:first-of-type { border-top: 0; }
.account-item:last-of-type { border-bottom: 0; }

// Layout
.account-login-wrapper,
.account-login,
.account-create {
  @include breakpoint(m) {
    display: block;
  }
}

.account-content {
  margin-top: 50px;
}

.account-page-create.account-nav-hidden .account-body { float: none; }

// todo: clean this up!
.account-create-form {
  .form-select-wrapper { max-width: none; }

  .form-date-year,
  .form-date-month {
    .form-select-wrapper {
      @include span-columns(4);
      margin-right: 2.35% !important;
    }
  }

  .form-date-day .form-select-wrapper { @include span-columns(4); }
}

.account-create-form,
.gift-content,
.shipping-quotes {
  // Forms.scss is using older markup. Todo: replace product forms with new version
  .form-actions .button-primary {
    height: 40px;
    padding: 0 15px;
    line-height: 40px;
  }

  .form-checkbox,
  .form-radio {
    display: none;
    width: auto;
    max-width: 0;
    min-width: 0;
    background: none;
    border: 0;
    appearance: none;
  }

  .form-radio + .form-label-text,
  .form-checkbox + .form-label-text {
    display: inline-block;
    margin-top: 5px;
    margin-bottom: 0;
    font-family: $secondary-font;
    font-size: $font-size - 2;

    &:before {
      @extend %unchecked;
      display: inline-block;
      margin-right: 3px;
      height: 10px;
      width: 11px;
      cursor: pointer;
      content: "";
    }
  }

  .form-radio,
  .form-checkbox {
    &:checked + .form-label-text:before { @extend %checked; }
  }

  .form-field-control { margin-top: 10px; }
  .form-field-checkbox { margin-bottom: 20px; }

  .required-text {
    margin-left: 5px;
    color: lighten($body-color, 25%);
    text-transform: none;
  }
}

/* scss-combine:modules/gift-certificates */
.gift-content {
  @include outer-container($blog-content-width);
  margin-bottom: 50px;

  .form-input {
    max-width: none;
  }

  // forms styeld in _account.scss
}

/* scss-combine:modules/baguette-box */
/*!
 * baguetteBox.js
 * @author  feimosi
 * @version 1.6.3
 * @url https://github.com/feimosi/baguetteBox.js
 */

#baguetteBox-overlay {
    display: none;
    opacity: 0;
    position: fixed;
    overflow: hidden;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1000000;
    background-color: #222;
    background-color: rgba(0,0,0,.8);
    transition: opacity .5s ease;

    &.visible {
        opacity: 1;
    }

    .full-image {
        display: inline-block;
        position: relative;
        width: 100%;
        height: 100%;
        text-align: center;

        figure {
            display: inline;
            margin: 0;      // needed for mobile
            height: 100%;   // Opera 12 image stretching fix
        }

        img {
            // IE8 fix
            display: inline-block;
            width: auto;
            height: auto;

            max-height: 100%;
            max-width: 100%;
            vertical-align: middle;
            box-shadow: 0 0 8px rgba(0,0,0,.6);
        }

        figcaption {
            display: block;
            position: absolute;
            bottom: 0;
            width: 100%;
            text-align: center;
            line-height: 1.8;
            white-space: normal;
            color: #ccc;
            background-color: #000;
            background-color: rgba(0,0,0,.6);
            font-family: sans-serif;
        }

        &:before {
            content: "";
            display: inline-block;
            height: 50%;
            width: 1px;
            margin-right:-1px;
        }
    }
}

#baguetteBox-slider {
    position: absolute;
    left: 0;
    top: 0;
    height: 100%;
    width: 100%;
    white-space: nowrap;
    transition: left .4s ease, transform .4s ease;

    &.bounce-from-right {
        animation: bounceFromRight .4s ease-out;
    }

    &.bounce-from-left {
        animation: bounceFromLeft .4s ease-out;
    }
}

@keyframes bounceFromRight{
    0% { margin-left: 0; }
    50% { margin-left: -30px; }
    100% { margin-left: 0; }
}

@keyframes bounceFromLeft{
    0% { margin-left: 0; }
    50% { margin-left: 30px; }
    100% { margin-left: 0; }
}

%arrow-button {
    top: 50%;
    top: calc(50% - 30px);
    width: 44px;
    height: 60px;
}

.baguetteBox-button {
    position: absolute;
    cursor: pointer;
    outline: none;
    padding: 0;
    margin: 0;
    border: 0;
    border-radius: 15%;
    background-color: #323232;
    background-color: rgba(50,50,50,.5);
    color: #ddd;
    font: 1.6em sans-serif;
    transition: background-color .4s ease;

    &:hover {
        background-color: rgba(50,50,50,.9);
    }

    &#next-button {
        @extend %arrow-button;
        right: 2%;
    }

    &#previous-button {
        @extend %arrow-button;
        left: 2%;
    }

    &#close-button {
        top: 20px;
        right: 2%;
        right: calc(2% + 6px);
        width: 30px;
        height: 30px;
    }

    // Firefox fix
    svg {
        position: absolute;
        left: 0;
        top: 0;
    }
}

/*
    Preloader
    Borrowed from http://tobiasahlin.com/spinkit/
*/

.baguetteBox-spinner {
    width: 40px;
    height: 40px;
    display: inline-block;
    position: absolute;
    top: 50%;
    left: 50%;
    margin-top: -20px;
    margin-left: -20px;
}

.baguetteBox-double-bounce1,
.baguetteBox-double-bounce2 {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background-color: #fff;
    opacity: .6;
    position: absolute;
    top: 0;
    left: 0;
    animation: bounce 2s infinite ease-in-out;
}

.baguetteBox-double-bounce2 {
    animation-delay: -1s;
}

@keyframes bounce {
    0%, 100% {
        transform: scale(0);
    } 50% {
        transform: scale(1);
    }
}
