core/structured-data/breadcrumbs:
{{!-- Breadcrumbs: include in base.html --}}

<script id="schema-breadcrumbs">
  var schema = document.createElement('script');
  schema.type = 'application/ld+json';
  schema.text = JSON.stringify({
    "@context": "http://schema.org",
    "@type": "BreadcrumbList",
    "itemListElement": [
      {{#each breadcrumbs}}
        {
          "@type": "ListItem",
          "position": {{@index}} + 1,
          "item": {
            "@id": {{#unless @last}}"{{url}}"{{else}}window.location.href{{/unless}},
            "name": "{{name}}"
          }
        }{{#unless @last}},{{/unless}}
      {{/each}}
    ]
  });

  document.querySelector('body').appendChild(schema);

  var script = document.getElementById('schema-breadcrumbs');
  script.parentElement.removeChild(script);
</script>

components/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
  <div class="currency-selector-wrapper">
    <label class="currency-selector-label" for="currency-switcher">
      <span data-currency-selector-toggle>{{currency_selector.active_currency_code}}
        {{> components/common/icons/down-arrow}}
      </span>
    </label>
      <select class="currency-selector">
        {{#each currency_selector.currencies}}
          <option value="{{switch_url}}" {{#if is_active}}selected{{/if}}>
            {{name}}
          </option>
        {{/each}}
      </select>
  </div>
{{/if}}

