components/common/banners:
{{#if location '===' 'top'}}
  {{#if this.top}}
    <div class="banners banners-top">
      <div class="banner rte">
        {{{limit this.top 1}}}
      </div>
    </div>
  {{/if}}
{{/if}}

{{#if location '===' 'bottom'}}
  {{#if this.bottom}}
    <div class="banners banners-bottom">
      <div class="banner rte">
        {{{limit this.bottom 1}}}
      </div>
    </div>
  {{/if}}
{{/if}}
