pages/product:
---
product:
  reviews:
    limit: 999
  related_products:
    limit: 4
  videos: true
---

{{#partial "page"}}

  {{> components/common/shipping-messages}}

  <div class="product-wrap" data-product-container>
    <div class="product-primary-wrapper">
      {{> components/common/breadcrumbs breadcrumbs}}

      <div class="product-primary">
        <div class="product-images">
          {{> components/products/images
            images=product.images
            default_image=../theme_settings.default_image
          }}
          {{> components/products/product-thumbnails product.images}}
        </div>

        <div class="product-details">
          {{> components/products/product-details}}
          {{> components/common/share-buttons
            share_buttons=settings.add_this
            url=product.url
            title=product.title
            image=(getImage product.main_image 'thumbnail')
          }}
        </div>
      </div>
    </div>

    <div class="product-secondary-wrapper">

      <nav class="product-tabs-menu">
        <ul class="product-tabs-list" data-tabs>
          <li class="product-tab-title">
            <a class="product-tab-link" href="#details">
              {{lang 'products.tabs.details'}}
            </a>
          </li>
          {{#if settings.show_product_rating}}
            <li class="product-tab-title">
              <a class="product-tab-link" href="#write_review">
                {{lang 'products.tabs.reviews'}} ({{product.num_reviews}})
              </a>
            </li>
          {{/if}}
          {{#if product.videos.featured}}
            <li class="product-tab-title">
              <a class="product-tab-link" href="#videos">
                {{lang 'products.tabs.videos'}}
              </a>
            </li>
          {{/if}}
        </ul>
        {{#any
          settings.show_product_rating
          product.description
          product.detail_messages
          product.videos.featured
        }}
          <div class="form-select-wrapper">
            <select class="product-tabs-select form-select" data-tabs>
              <option class="product-tab-link" value="#details">
                {{lang 'products.tabs.details'}}
              </option>
              {{#if settings.show_product_rating}}
                <option class="product-tab-link" value="#write_review">
                  {{lang 'products.tabs.reviews'}} ({{product.num_reviews}})
                </option>
              {{/if}}
              {{#if product.videos.featured}}
                <option class="product-tab-link" value="#videos">
                  {{lang 'products.tabs.videos'}}
                </option>
              {{/if}}
            </select>
          </div>
        {{/any}}
      </nav>

      <div class="product-tabs tabs-content">
        <div class="tab-content-panel rte" id="details">
          {{#if product.description}}
            <div class="product-description">
              {{{product.description}}}
              {{{snippet 'product_description'}}}
            </div>
          {{/if}}
          {{#if product.detail_messages}}
            <div class="product-description">
              {{{product.detail_messages}}}
            </div>
          {{/if}}
        </div>
        {{#if settings.show_product_rating}}
          <div class="tab-content-panel" id="write_review">
            <div class="product-reviews">
              <h3 class="section-title">
                {{lang 'product.reviews.customer_reviews'}} ({{product.num_reviews}})
              </h3>
              {{> components/products/reviews
                reviews=product.reviews
              }}
            </div>
          </div>
        {{/if}}
        {{#if product.videos.featured}}
          <div class="tab-content-panel" id="videos">
            {{#each product.videos.list}}
              <div class="product-video-wrapper">
                <iframe
                  src="https://www.youtube.com/embed/{{id}}?rel=0&showinfo=0&autohide=1"
                  frameborder="0"
                  allowfullscreen
                ></iframe>
              </div>
            {{/each}}
          </div>
        {{/if}}
      </div>
      {{{snippet 'product_details'}}}
    </div>
  </div>

  {{> components/products/products-related
    products=product.related_products
    quick_shop=../theme_settings.product-quick-shop
  }}

  {{#each product.reviews.messages}}
    <div class="product-review-messages" data-review-messages>
      {{#if error}}
        {{> core/alerts/alert-error message=error dismissable=true}}
      {{/if}}
      {{#if success}}
        {{> core/alerts/alert-success message=success dismissable=true}}
      {{/if}}
    {{/each}}
  </div>

  {{> core/structured-data/product}}
  {{> core/structured-data/product-reviews}}

{{/partial}}
{{> layout/base}}

components/common/shipping-messages:
{{#if shipping_messages}}
  {{#each shipping_messages}}
    <div class="promo-message">{{{message}}}</div>
  {{/each}}
{{/if}}

{{#if cart.status_messages}}
  {{#each cart.status_messages}}
    {{#if type "==" 2}}
      <div class="promo-message">{{{message}}}</div>
    {{/if}}
  {{/each}}
{{/if}}

{{#if product.shipping_messages}}
  {{#each product.shipping_messages}}
    <div class="promo-message">{{{message}}}</div>
  {{/each}}
{{/if}}

components/products/products-related:
{{#if products.length}}
  <div class="products-related-wrap">
    <section class="products-related">
      <h2 class="section-title">{{lang 'product.related_products'}}</h2>
      <div class="product-grid grid-4">
        {{#each products}}
          {{> components/products/product-item
              this quick_shop=../theme_settings.product-quick-shop
              default_image=../theme_settings.default_image
              price_as_low_as=../theme_settings.price_as_low_as
        }}
        {{/each}}
      </div>
    </section>
  </div>
{{/if}}

core/structured-data/product:
{{!-- Product: include in product.html page template --}}

<script id="schema-product">

  var schema = document.createElement('script');
  schema.type = 'application/ld+json';
  schema.text = JSON.stringify({
    "@context": "http://schema.org/",
    "@type": "Product",
    "name": "{{product.title}}",
    "image": "{{getImage product.main_image}}",
    "description": "{{json product.description}}",
    "sku": "{{product.sku}}",
    "brand": {
      "@type": "Thing",
      "name": "{{product.brand.name}}"
    },
    {{#if product.reviews.list}}
      "aggregateRating": {
        "@type": "AggregateRating",
        "ratingValue": "{{product.rating}}",
        "reviewCount": "{{product.num_reviews}}"
      },
    {{/if}}
    "offers": {
      "@type": "Offer",
      "priceCurrency": "{{currency_selector.active_currency_code}}",
      "price": "{{#if product.price.without_tax}}{{product.price.without_tax.value}}{{/if}}{{#if product.price.with_tax}}{{#unless product.price.without_tax}}{{product.price.with_tax.value}}{{/unless}}{{/if}}",
      "itemCondition": "{{#if product.condition '===' 'New'}}http://schema.org/NewCondition{{/if}}{{#if product.condition '===' 'Used'}}http://schema.org/UsedCondition{{/if}}{{#if product.condition '===' 'Refurbished'}}http://schema.org/RefurbishedCondition{{/if}}",
      "availability": "{{#unless product.out_of_stock_message}}http://schema.org/InStock{{else}}http://schema.org/OutOfStock{{/unless}}",
      "seller": {
        "@type": "Organization",
        "name": "{{settings.store_name}}"
      }
    }
  });

  document.querySelector('body').appendChild(schema);

  var script = document.getElementById('schema-product');
  script.parentElement.removeChild(script);
</script>

core/structured-data/product-reviews:
{{!-- Product Reviews: include in product.html page template --}}

{{#if settings.show_product_reviews}}
  {{#each product.reviews.list}}
    <script type="application/ld+json">
      {
        "@context": "http://schema.org/",
        "@type": "Review",
        "itemReviewed": {
          "@type": "Product",
          "image": "{{getImage ../product.main_image}}",
          "name": "{{../product.title}}"
        },
        "reviewRating": {
          "@type": "Rating",
          "ratingValue": "{{rating}}"
        },
        "name": "{{title}}",
        "author": {
          "@type": "Person",
          "name": "{{name}}"
        },
        "reviewBody": "{{text}}",
        "publisher": {
          "@type": "Organization",
          "name": "{{../settings.store_name}}"
        }
      }
    </script>
  {{/each}}
{{/if}}

components/products/review:
{{#if @index "<" review_count}}
  <article class="review default">
{{/if}}
{{#if @index ">=" review_count}}
  <article class="review supplementary">
{{/if}}
  <header class="review-header">
    <h1 class="review-title">{{title}}</h1>
    <div class="review-rating">
      {{> components/products/ratings rating=rating}}
    </div>
    {{#if name}}
      <span class="review-meta">{{lang 'products.reviews.post_on_by' name=name date=date}}</span>
    {{else}}
      <time class="review-date">{{date}}</time>
    {{/if}}
  </header>
  <section class="review-comment">{{text}}</section>
</article>

core/forms/common/multiline:
{{!
  Textarea form component

  @param validation_message string
         Custom validation message
}}
<div class="form-field form-field-common form-field-multiline {{#if required}}form-required{{/if}}" id="{{id}}">
  <label class="form-label">
    <span class="form-field-title">
      {{label}}
      {{#if required}}<span class="required-text">{{lang 'common.required'}}</span>{{/if}}
    </span>
    <span class="form-field-control">
      <textarea
        class="form-input"
        id="textarea-{{id}}"
        name="{{name}}"
        placeholder="{{prefill}}"
        rows="{{rows}}"
        {{#if required}}
          required
          {{#if validation_message}}
            data-vd-message-required="{{validation_message}}"
          {{/if}}
        {{/if}}
        aria-required="{{required}}">{{value}}</textarea>
    </span>
  </label>
</div>

