components/brand/product-listing:
{{#if brand.products}}
  <form class="form" action="/compare" method="POST">
    <div class="product-grid grid-{{theme_settings.products-per-row}}">
      {{#each brand.products}}
        {{> components/products/product-item
            this
            show_compare=../show_compare
