  {{> components/common/pagination pagination.brand}}
{{/if}}

components/products/price:
{{!
  Shows a product's price.

  Prices are fairly complicated. Besides having to be able to show the price
  with and without tax, a merchant can also assign a Recommended Retail Price,
  and a Sale Price.

  This means that in extreme cases, we have to show 4 different prices at once.

  When a Sale Price is set we do not have direct access to it--instead it
  overwrites the regular price.

  @param price object

  @param show_tax_labels bool
         If true, show a label saying the price is with or without tax.

  @param show_savings bool
         If true, show a label saying how much is saved from the full price.
}}
{{#if price}}
  {{#or has_options product.options.length}}
    <div class="price {{#if price.without_tax}}{{#if price.with_tax}}multiple-prices{{/if}}{{/if}}" {{#if schema_org}} itemprop="offers" itemscope itemtype="http://schema.org/Offer" {{/if}}>
      {{#if schema_org}}
        <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">

        {{#if price.without_tax}}
          <meta itemprop="price" content="{{price.without_tax.value}}">
        {{else}}
          <meta itemprop="price" content="{{price.with_tax.value}}">
        {{/if}}
      {{/if}}

      {{#if price.without_tax}}
        <span class="price-withouttax" data-product-price-wrapper="without-tax">

          {{#if page_type '!==' 'product'}}
            {{#if price_as_low_as}}
              <span>{{lang 'product.as_low_as_price_message'}}</span>
            {{/if}}
          {{/if}}

          {{#if price.rrp_without_tax}}
            <span class="price-rrp" data-price-rrp>
              {{price.rrp_without_tax.formatted}}
            </span>
          {{/if}}

          <span class="price-value" data-product-price>
            {{price.without_tax.formatted}}
          </span>

          {{#if show_tax_labels}}
            {{#if price.with_tax}}
              <span class="price-tax-label">{{lang 'product.excluding_tax'}}</span>
            {{/if}}
          {{/if}}
        </span>
      {{/if}}

      {{#if price.with_tax}}
        <span class="price-withtax" data-product-price-wrapper="with-tax">

          {{#if page_type '!==' 'product'}}
            {{#if price_as_low_as}}
              <span>{{lang 'product.as_low_as_price_message'}}</span>
            {{/if}}
          {{/if}}

          {{#if price.rrp_with_tax}}
            <span class="price-rrp" data-price-rrp>
              {{price.rrp_with_tax.formatted}}
            </span>
          {{/if}}

          <span class="price-value" data-prodct-price>
            {{price.with_tax.formatted}}
          </span>

          {{#if show_tax_labels}}
            {{#if price.without_tax}}
              <span class="price-tax-label">{{lang 'product.including_tax'}}</span>
            {{/if}}
          {{/if}}
        </span>
      {{/if}}

      {{#if show_savings}}
        {{#if price.saved}}
          <span class="price-saved" data-product-price-saved>
            {{lang 'core.price.you_save' amount=price.saved.formatted}}
          </span>
        {{/if}}
      {{/if}}
    </div>
  {{else}}
    <div class="price {{#if price.without_tax}}{{#if price.with_tax}}multiple-prices{{/if}}{{/if}}" {{#if schema_org}} itemprop="offers"
         itemscope itemtype="http://schema.org/Offer" {{/if}} >
      {{#if schema_org}}
      
        {{#if product.can_purchase}}
          <meta itemprop="availability" content="http://schema.org/InStock">
        {{/if}}
        
        <meta itemprop="itemCondition" content="http://schema.org/NewCondition">
        
        <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">

        {{#if price.without_tax}}
          <meta itemprop="price" content="{{price.without_tax.value}}">
        {{else}}
          <meta itemprop="price" content="{{price.with_tax.value}}">
        {{/if}}
      {{/if}}

      {{#if price.without_tax}}
        <span class="price-withouttax" data-product-price-wrapper="without-tax">

          {{#if price.rrp_without_tax}}
            <span class="price-rrp" data-price-rrp>
              {{price.rrp_without_tax.formatted}}
            </span>
          {{/if}}

          <span class="price-value" data-product-price>
            {{price.without_tax.formatted}}
          </span>

          {{#if show_tax_labels}}
            {{#if price.with_tax}}
              <span class="price-tax-label">{{lang 'product.excluding_tax'}}</span>
            {{/if}}
          {{/if}}
        </span>
      {{/if}}

      {{#if price.with_tax}}
        <span class="price-withtax" data-product-price-wrapper="with-tax">

          {{#if price.rrp_with_tax}}
            <span class="price-rrp" data-price-rrp>
              {{price.rrp_with_tax.formatted}}
            </span>
          {{/if}}

          <span class="price-value" data-prodct-price>
            {{price.with_tax.formatted}}
          </span>

          {{#if show_tax_labels}}
            {{#if price.without_tax}}
              <span class="price-tax-label">{{lang 'product.including_tax'}}</span>
            {{/if}}
          {{/if}}
        </span>
      {{/if}}

      {{#if show_savings}}
        {{#if price.saved}}
          <!--<span class="price-saved" data-product-price-saved>
            {{lang 'core.price.you_save' amount=price.saved.formatted}}
          </span>-->
        {{/if}}
      {{/if}}
    </div>
  {{/or}}
{{/if}}
{{#if call_for_price}}
  <div class="price call-for-price">
    {{call_for_price}}
  </div>
{{/if}}

components/products/product-item:
{{!
  @param default_image A default product image.
}}
<article class="product-item">
  <a class="product-item-link" href="{{url}}">
    <figure class="product-item-thumbnail">
      {{#if quick_shop}}
        <span class="quick-shop-trigger" data-quick-shop data-product-id="{{id}}">
          {{lang 'product.quick_shop'}}
        </span>
      {{/if}}

      <img
        class="product-item-image"
        src="{{getImage image 'medium' (cdn default_image)}}"
        alt="{{image.alt}}"
        title="{{image.alt}}"
      >

      {{#if price.saved}}
        <span class="product-item-sale">{{lang 'product.sale'}}</span>
      {{/if}}
    </figure>

    <div class="product-item-details">
      <h3 class="product-item-title">{{name}}</h3>
      {{# if num_reviews ">" 0}}
        <div class="product-item-rating">
          {{> components/products/ratings rating=rating aggregate=true}}
          {{{snippet 'product_rating'}}}
        </div>
      {{/if}}
    </div>
  </a>
  <form
    class="form product-form"
      method="post"
      action="/cart.php"
      enctype="multipart/form-data"
      data-cart-item-add={{id}}>
      <input type="hidden" name="action" value="add">
      <input type="hidden" name="product_id" value="{{id}}" data-product-id>
      
   {{> components/products/price
      show_tax_labels=true
      price_as_low_as=price_as_low_as
      schema_org=false
    }}

      <div class="form-field product-quantity-wrapper" data-product-quantity>
      <label class="form-label">
        <span class="form-field-title">{{lang 'product.quantity'}}</span>
        {{> components/common/quantity product}}
      </label>
    </div>
    
    {{#if show_cart_action}}
        {{#if pre_order}}
          <input class="button pre-order" type="submit" value="{{lang 'product.pre_order'}}" data-button-purchase data-button-preorder>
          {{release_date}}
        {{else}}
          <input class="button" type="submit" value="{{lang 'product.add_to_cart'}}" data-button-purchase>
        {{/if}}

        {{#if settings.show_wishlist}}
          <a class="button inverted" href="{{add_to_wishlist_url}}" data-wishlist-add>{{lang 'product.add_to_wishlist'}}</a>
        {{/if}}

        <span class="loading"></span>
      {{{snippet 'product_addtocart'}}}

      <div class="product-alert" data-product-cart-message></div>
      <div class="product-alert" data-product-option-message></div>
    {{else}}
        <input type="button" disabled="disabled" value="Sold Out" class="button disabled" />
      {{/if}}
    
    <!--{{#if stock_level ">" 0}}
      <input class="button" type="submit" value="Add to Bag" data-button-purchase="">
    {{else}}
      {{#if stock_level "===" null}}
        <input class="button" type="submit" value="Add to Bag" data-button-purchase="">
      {{else}}
        <input class="button disabled" type="submit" value="Sold Out" disabled="disabled">
      {{/if}}
    {{/if}}-->
</form>

  {{#if show_compare}}
    <div
      class="form-inline
      form-field
      form-field-options
      form-field-checkbox
      product-item-compare"
    >
      <div class="form-field-control">
        <label
          class="form-label {{class}}"
          for="compare-{{id}}"
          data-text="{{lang "product.compare"}}"
          data-alt-text="{{lang "product.comparing"}}"
        >
          <input
            class="form-input form-checkbox"
            type="checkbox"
            name="products[]"
            value="{{id}}"
            id="compare-{{id}}"
          >
          <span class="form-label-text">{{lang 'product.compare'}}</span>
        </label>
      </div>
    </div>
  {{/if}}
    
</article>
